function makeMasonry(){if(document.querySelector(".galleri-images")){const masonry=document.querySelector(".galleri-images"),columns=getComputedStyle(masonry).getPropertyValue("--columns"),images=masonry.querySelectorAll("img");var column_heights=[];for(let i=0;i<columns;i++){const column=document.createElement("div");column.classList.add("column"+i),masonry.appendChild(column),column_heights.push(0)}images.forEach((img=>{const choosen_column_key=0;for(let i=0;i<column_heights;i++)column_heights[i]<=0&&(choosen_column_key=i);document.querySelector(".column"+choosen_column_key).appendChild(img),column_heights[choosen_column_key]+=img.getBoundingClientRect().height})),console.log(column_heights)}}document.addEventListener("DOMContentLoaded",(()=>{makeMasonry()}));